import axios from "axios";
import React, { useState } from "react";
import { Container, Row, Col, Form, Button } from "react-bootstrap";
import { EnvelopeAt } from "react-bootstrap-icons";

// class Contact extends Component {
// // constructor(props) {
// //   super(props);
// //   this.state = {
// //     fullName: "",
// //     email: "",
// //     typeOfInquiry: "",
// //     inquiryDescription: ""
// //     // mailSent: false,
// //     // error: null
// //   }
// // }

// // handleContactFormSubmit( event ) {
// //   event.preventDefault();
// //   console.log(this.state);
// // }

// constructor(props){
//   super(props);
//   this.state = {
//     contact_forms: [],
//   }
// }

//   componentDidMount(){
//     const url = "http://localhost/api/contact.php"
//     axios.post(url).then(response => response.data)
//     .then((data) => {
//       this.setState({contact_forms: data})
//       console.log(this.state.contact_forms)
//     })
//   }

// render(){

  const Contact = () => {

    const [inputs, setInputs] = useState("");

    const handleChange = (event) => {
      const name = event.target.name;
      const value = event.target.value;
      setInputs(values => ({...values, [name]: value}))
    }

    const handleSubmit = (event) => {
      event.preventDefault();

      axios.post("http://localhost:8000/the-other-nuub-api/api-contact.php", inputs);
      console.log(inputs)
    }

     
  return (
    <section id="contact-and-support">
      <Container
        fluid
        style={{
          paddingTop: "5rem",
          height: 1400,
          fontFamily: "primary-font",
        }}
      >
        <Row>
          <Col xs={4} lg={8}>
            <div
              style={{
                backgroundColor: "#F9E95D",
                width: 900,
                height: 1505,
                position: "absolute",
                top: 4500,
                left: 0,
                zIndex: -1,
              }}
            ></div>
            <h1
              style={{
                fontSize: 55,
                marginLeft: "8rem",
                position: "relative",
              }}
            >
              Would you like to get in touch with us? Or you need assistance?
            </h1>
            <p
              className="w-75"
              style={{
                fontSize: 25,
                marginTop: "2rem",
                marginLeft: "8rem",
                fontFamily: "secondary-font",
                position: "relative",
              }}
            >
              You can actually do both, feel free to contact us, regardless of
              needing assistance or just a small talk with us.
            </p>
            <Form
            onSubmit = {handleSubmit}
              style={{
                width: "45%",
                padding: "0 3rem",
                marginTop: "3rem",
                marginLeft: "5rem",
                fontSize: 25,
              }}
            >
              <Form.Group
                style={{
                  marginBottom: "2rem",
                }}
              >
                <Form.Label>Your full name</Form.Label>
                <Form.Control
                  type="text"
                  id="fullName"
                  name="full_name"
                  // value={this.state.fullName}
                  onChange={handleChange}
                  style={{
                    borderBottom: "3px solid black",
                    height: "3rem",
                  }}
                ></Form.Control>
              </Form.Group>
              <Form.Group
                style={{
                  marginBottom: "2rem",
                }}
              >
                <Form.Label>Your email address</Form.Label>
                <Form.Control
                  name="email"
                  id="email"
                  type="email"
                  // value={this.state.email}
                  onChange={handleChange}
                  style={{
                    borderBottom: "3px solid black",
                    height: "3rem",
                  }}
                ></Form.Control>
              </Form.Group>
              <Form.Group
                style={{
                  marginBottom: "2rem",
                }}
              >
                <Form.Label>Select the type of inquiry</Form.Label>
                <Form.Select
                  name="type_of_inquiry"
                  id="typeOfInquiry"
                  // value={this.state.typeOfInquiry}
                  onChange={handleChange}
                  style={{
                    borderBottom: "3px solid black",
                    height: "3rem",
                    fontSize: 20,
                  }}
                >
                  <option>Appointment</option>
                  <option>Addressing a question</option>
                  <option>Support/Assistance</option>
                </Form.Select>
              </Form.Group>
              <Form.Group
                style={{
                  marginBottom: "2rem",
                }}
              >
                <Form.Label>Describe your inquiry</Form.Label>
                <Form.Control
                  type="text"
                  name="inquiry_description"
                  id="inquiryDescription"
                  // value={this.state.inquiryDescription}
                  onChange={handleChange}
                  style={{
                    borderBottom: "3px solid black",
                    height: "10rem",
                  }}
                ></Form.Control>
              </Form.Group>
              <Button
                type="submit"
                value="Submit"
                style={{
                  fontSize: 30,
                  fontWeight: "bold",
                  backgroundColor: "#7F7EC7",
                  color: "whitesmoke",
                  borderBottom: "4px solid black",
                  marginTop: "1rem",
                }}
              >
                Submit
              </Button>{" "}
            </Form>
          </Col>

          <Col xs={4} lg={8}>
            <EnvelopeAt
              size={500}
              style={{
                position: "absolute",
                left: 800,
                top: 5280,
              }}
            ></EnvelopeAt>
          </Col>
        </Row>
      </Container>
    </section>
  );
};

export default Contact;
