import React, { useState, useEffect } from "react";
import { Nav, Navbar, Button } from "react-bootstrap";
import { XLg } from "react-bootstrap-icons";
import { useTranslation } from "react-i18next";

const AuthenticationNav = () => {
  const { i18n } = useTranslation();
  const [state, setState] = useState(false);

  const handleChangeLanguage = (lng) => {
    i18n.changeLanguage(lng);
    localStorage.setItem("lng", lng);
    setState(!state);
  };

  useEffect(() => {
    const lang = localStorage.getItem("input");
    if (lang) {
      setState(JSON.parse(lang));
    }
  }, []);

  useEffect(() => {
    localStorage.setItem("input", JSON.stringify(state));
  });

  return (
    <Navbar
      className="navbar navbar-light pt-4"
      expand="lg"
      style={{ position: "absolute", left: "95%" }}
    >
      <Navbar.Toggle aria-controls="navbar-nav" />
      <Nav className="nav" style={{ fontSize: 24, fontWeight: "bold" }}>
        <Nav.Link href="/" className="me-4">
          <XLg size={45}></XLg>
        </Nav.Link>
      </Nav>
      <Nav className="language ms-2 me-3">
        {state ? (
          <Button
            className="me-3 px-4 py-2"
            style={{ fontSize: 22 }}
            variant="info"
            active
            onClick={() => handleChangeLanguage("da")}
          >
            {" "}
            DA{" "}
          </Button>
        ) : (
          <Button
            className="me-3 px-4 py-2"
            style={{ fontSize: 22 }}
            variant="info"
            disabled
            onClick={() => handleChangeLanguage("da")}
          >
            {" "}
            DA{" "}
          </Button>
        )}
        {state ? (
          <Button
            className="me-4 px-4 py-2"
            style={{ fontSize: 22 }}
            variant="info"
            disabled
            onClick={() => handleChangeLanguage("en")}
          >
            {" "}
            EN{" "}
          </Button>
        ) : (
          <Button
            className="me-4 px-4 py-2"
            style={{ fontSize: 22 }}
            variant="info"
            active
            onClick={() => handleChangeLanguage("en")}
          >
            {" "}
            EN{" "}
          </Button>
        )}
      </Nav>
    </Navbar>
  );
};

export default AuthenticationNav;
