import { createContext, useState } from "react";

import { useNavigate } from "react-router-dom";
import axios from "axios";

const FormContext = createContext({});

export const FormProvider = ({ children }) => {
  const [page, setPage] = useState(0);
  const [appointmentInput, setAppointmentInput] = useState({
    meeting_date: "",
    meeting_time: "",
    meeting_description: "",
  });

  const FormTitle = [
    "When would you like to have the meeting?",
    "Can you tell us a small description about your project?",
  ];

  const [errorMessage, setErrorMessage] = useState("");
  const [disableButton, setDisableButton] = useState(false);
  const navigate = useNavigate();

  const handleAppointmentSubmit = (event) => {
    event.preventDefault();

    axios
      .post("http://localhost/api/add_meeting.php", { withCredentials: true })
      .then((response) => {
        if (response.data) {
          navigate("/appointmentconfirm");
          setErrorMessage("");
        } else {
          setErrorMessage("Appointment submitting failed");
          setDisableButton(true);
        }
      })
      .catch((error) => {
        if (
          error.response &&
          error.response.data === "You must choose a date"
        ) {
          setErrorMessage("You must choose a date");
          setDisableButton(true);
        }
      });
  };

  const handleChangeInput = (event) => {
    const name = event.target.name;
    const value = event.target.value;
    setAppointmentInput((prevAppointmentInput) => ({
      ...prevAppointmentInput,
      [name]: value,
    }));
  };

  return (
    <FormContext.Provider
      value={{
        page,
        setPage,
        appointmentInput,
        setAppointmentInput,
        handleChangeInput,
        handleAppointmentSubmit,
        errorMessage,
        FormTitle,
      }}
    >
      {children}
    </FormContext.Provider>
  );
};

export default FormProvider;
