import React, { useState, useEffect } from 'react';
import LoggedInNavigation from "./LoggedInNavigation";
import {Row, Col, Container, Button} from "react-bootstrap";
import { PersonFill } from "react-bootstrap-icons";
import { useNavigate } from "react-router-dom";

const Profile = () => {

    const navigate = useNavigate();
    const[first_name, setFirstName] = useState("");
    const[last_name, setLastName] = useState("");
    const[company, setCompany] = useState("");
    const[email, setEmail] = useState("");
    const[phone_number, setPhoneNumber] = useState("");
    const[isLoggedIn, setIsLoggedIn] = useState(false);

    useEffect(() => {
        const storedIsLoggedIn = sessionStorage.getItem("isLoggedIn");
        if (storedIsLoggedIn) {
            setIsLoggedIn(true);
        } else {
            navigate("/login")
        }
        
        const first_name = sessionStorage.getItem("first_name");
        const last_name = sessionStorage.getItem("last_name");
        const company = sessionStorage.getItem("company");
        const email = sessionStorage.getItem("email");
        const phone_number = sessionStorage.getItem("phone_number");

        setFirstName(first_name);
        setLastName(last_name);
        setCompany(company);
        setEmail(email);
        setPhoneNumber(phone_number);

        console.log("Huuray, logged in!");

    }, [navigate]);

  return (
    <>
    <LoggedInNavigation />
    <Container>
        <Row>
            <Col sm={12} md={10} lg={12}>
            <div className="user mt-5 d-flex align-items-center flex-column">
                <div style={{backgroundColor: "#F1F1F1", borderRadius: 100, width: 200, height: 200}}>
                    <PersonFill size={152} className="mt-4 ms-4"/>
                    </div>
                <h1 className="mt-4" style={{fontSize: 60}}>{first_name} {last_name}</h1>
            </div>
            <div className="user-section d-flex align-items-center flex-column m-4">
                <h2>First Name</h2>
                <p style={{fontSize: 25, marginBottom: "2rem"}}>{first_name}</p>

                <h2>Last Name</h2>
                <p style={{fontSize: 25, marginBottom: "2rem"}}>{last_name}</p>

                <h2>Company</h2>
                <p style={{fontSize: 25, marginBottom: "2rem"}}>{company}</p>

                <h2>Email address</h2>
                <p style={{fontSize: 25, marginBottom: "2rem"}}>{email}</p>

                <h2>Phone number</h2>
                <p style={{fontSize: 25, marginBottom: "0.5rem"}}>{phone_number}</p>

                <Button href="/updateprofile" className="update_profile px-3 py-2 mt-4" style={{backgroundColor: "#7F7EC7", borderBottom: "3px solid black", fontSize: 25, fontWeight: "bold", borderRadius: 20}}>
                    Update Profile
                </Button> 
            </div>
            </Col>
            </Row>
        </Container>
    </>
  )
}

export default Profile
