import React, { useState } from "react";
import LoggedInNavigation from "./LoggedInNavigation";
import { Container, Button, Stack, Row, Col, Form } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const EditAppointment = () => {
  const [errorMessage, setErrorMessage] = useState("");
  const [appointmentInput, setAppointmentInput] = useState({
    meeting_date: "",
    meeting_time: "",
  });
  const navigate = useNavigate();

  const handleChangeInput = (event) => {
    const name = event.target.name;
    const value = event.target.value;
    setAppointmentInput((prevAppointmentInput) => ({
      ...prevAppointmentInput,
      [name]: value,
    }));
  };

  const handleAppointmentSubmit = (event) => {
    event.preventDefault();

    axios
      .post(
        "http://localhost/api/update_meeting.php",
        new URLSearchParams({
          meeting_date: appointmentInput.meeting_date,
          meeting_time: appointmentInput.meeting_time,
        }),
        { withCredentials: true }
      )
      .then((response) => {
        if (response.data) {
          navigate("/appointmentconfirm");
          setErrorMessage("");
        } else {
          setErrorMessage("Appointment submitting failed");
        }
      })
      .catch((error) => {
        if (
          error.response &&
          error.response.data === "You must choose a date"
        ) {
          setErrorMessage("You must choose a date");
        } else if (
          error.response &&
          error.response.data === "You must choose a time"
        ) {
          setErrorMessage("You must choose a time");
        } else if (
          error.response &&
          error.response.data === "Meetings are not available on weekends"
        ) {
          setErrorMessage("Meetings are not available on weekends");
        } else if (
          error.response &&
          error.response.data ===
            "Meetings are available between 10 AM and 3 PM"
        ) {
          setErrorMessage("Meetings are available between 10 AM and 3 PM");
        } else {
          console.log(error);
        }
      });
  };

  return (
    <>
      <LoggedInNavigation />
      <Container>
        <h1 style={{ marginTop: "5rem", textAlign: "center", fontSize: 50 }}>
          Change appointment
        </h1>
        <p className="mb-4 mt-4 mx-4 px-5" style={{ fontSize: 30 }}>
          In the case you’re not able to come to the original time, you can edit
          your meeting here with a new time or date.
        </p>
        <Row>
          <Col sm={2} md={12} lg={12}>
            <Form
              onSubmit={handleAppointmentSubmit}
              className="px-5 my-4 px-5"
              style={{
                fontSize: 25,
                fontFamily: "primary-font",
                marginLeft: "1rem",
                marginRight: "1rem",
              }}
            >
              <Stack
                direction="horizontal"
                gap={4}
                className="d-flex justify-content-center mb-5"
              >
                <Form.Group>
                  <Form.Control
                    size="lg"
                    type="date"
                    name="meeting_date"
                    value={appointmentInput.meeting_date}
                    onChange={handleChangeInput}
                    style={{
                      marginBottom: "1rem",
                      padding: "1rem",
                      fontSize: 25,
                      borderRadius: 20,
                      borderBottom: "3px solid grey",
                    }}
                  />
                </Form.Group>
                <Form.Group>
                  <Form.Control
                    size="lg"
                    type="time"
                    name="meeting_time"
                    value={appointmentInput.meeting_time}
                    onChange={handleChangeInput}
                    style={{
                      marginBottom: "1rem",
                      padding: "1rem",
                      fontSize: 25,
                      borderRadius: 20,
                      borderBottom: "3px solid grey",
                    }}
                  />
                </Form.Group>
              </Stack>

              <Stack
                direction="horizontal"
                gap={5}
                className="d-flex justify-content-center"
              >
                <Button
                  className="mt-4 px-4"
                  style={{
                    fontSize: 30,
                    fontFamily: "primary-font",
                    borderBottom: "4px solid black",
                    borderRadius: 20,
                    backgroundColor: "#7F7EC7",
                    marginBottom: "2rem",
                  }}
                  onClick={handleAppointmentSubmit}
                >
                  Confirm
                </Button>
              </Stack>
              <p
                style={{
                  color: "red",
                  fontSize: 30,
                  fontFamily: "secondary-font",
                  fontWeight: "bold",
                  textAlign: "center",
                }}
              >
                {errorMessage}
              </p>
            </Form>
          </Col>
        </Row>
      </Container>
    </>
  );
};

export default EditAppointment;
