import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import LoggedInNavigation from "./LoggedInNavigation";
import { Row, Col, Container } from "react-bootstrap";
import axios from "axios";

function Dashboard() {
  const navigate = useNavigate();
  const [user, setUser] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost/api/get_user.php", { withCredentials: true })
      .then((response) => {
        if (response.data) {
          const user = response.data;
          setUser(user);
          navigate("/dashboard");
        } else {
          navigate("/login");
        }
      });
  }, [navigate]);

  // creating another useEffect to avoid the user from going to login page being logged in already
  useEffect(() => {
    axios.get("http://localhost/api/check_logged_users.php", {withCredentials: true})
    .then((response) => {
      if(response.data) {
        navigate("dashboard");
      }
    })
  }, [navigate]);

  return (
    <>
      <LoggedInNavigation />

      <Container>
        <Row>
          <Col sm={5} md={10} xl={12}>
            <div
              className="d-flex align-items-center flex-column p-5"
              style={{ textAlign: "center", marginTop: "5rem" }}
            >
              <h1 className="mb-5" style={{ fontSize: 50 }}>
                Welcome to your Nuub dashboard, {user.first_name}
              </h1>
              <p style={{ fontSize: 30, width: "90%" }}>
                Here you can update your creditentials, create a new meeting
                with us and manage your appointment :D
              </p>
            </div>
          </Col>
        </Row>
      </Container>
    </>
  );
}

export default Dashboard;
