import React, { useState, useEffect } from 'react';
import { useNavigate } from "react-router-dom";
import LoggedInNavigation from "./LoggedInNavigation";
import { Row, Col, Container } from "react-bootstrap"; 

function Dashboard() {

    const navigate = useNavigate();
    const[mount, setMount] = useState(false);

    useEffect(() => {
        setMount(true);
    }, []);

    const email = sessionStorage.getItem("email");
    if(!email && mount){
        return navigate("/login");
    }

  return (
    <>
    <LoggedInNavigation />
    
    <Container>
      <Row>
        <Col sm={5} md={7} xl={12}>
          <div className="d-flex align-items-center flex-column p-5 mt-4" style={{textAlign: "center"}}>
            <h1 className="mb-5" style={{fontSize: 45}}>Welcome to your Nuub dashboard, user!</h1>
            <p>Here you can update your creditentials, create a new meeting with us and manage your appointment :D</p>
            </div>
        </Col>
        </Row>
      </Container>
    </>
  )
}

export default Dashboard
