import React, { useState, useEffect } from "react";
import NavigationPriceCalculator from "./NavigationPriceCalculator";
import { Container, Button } from "react-bootstrap";
import axios from "axios";

const PriceCalculator = () => {

  const [currentStep, setCurrentStep] = useState(-1);
  const [questionList, setQuestionList] = useState([]);
  const [answerOptions, setAnswerOptions] = useState([]);

  useEffect(() => {
    axios.get("http://localhost/bachelor_exam/questions.php")
    .then(response => { 
    setQuestionList(response.data);
    setCurrentStep(0)
  })  .catch(error => {
        console.log(error);
      });
}, [currentStep]);

useEffect(() => {
  if (questionList[currentStep]) {
  axios.get(`http://localhost/bachelor_exam/answers.php?question_id=${questionList[currentStep].id}`)
  .then(response => {
    setAnswerOptions(response.data);
  })
  .catch(error => {
    console.log(error);
  })
}
}, [currentStep, questionList]);


const handleNextSection = () => {
  if(currentStep !== -1 && currentStep + 1 < questionList.length) {
  setCurrentStep(currentStep + 1);
  }
};

const handlePreviousSection = () => {
  if(currentStep !== -1 && currentStep > 0){
  setCurrentStep(currentStep - 1);
  }
};


  return (
    <div>
      <NavigationPriceCalculator 
      currentStep={currentStep}
      handleNextSection={handleNextSection}
      handlePreviousSection={handlePreviousSection}
      />
      <Container fluid style={{
        borderTop: "4px solid black",
        borderBottom: "4px solid black",

      }}>
        {currentStep === -1 || !questionList.length ? (
          <p>Please wait...</p>
        ) : (
          <>
        <h1 className="question_title">{questionList[currentStep].text}</h1>
        <h3 className="question_description">{questionList[currentStep].description}</h3>
        {questionList[currentStep].question_type === "multiple_choice" ? (
          <>
          {answerOptions.map((answer) => (
            <div key={`${answer.id} - ${questionList[currentStep].question_id}-${index}`}>
              <input type="radio" id={answer.id} name={questionList[currentStep].question_id} value={answer.id} />
              <label htmlFor={answer.id}>{answer.text}</label>
              </div>
          ))}
              </>
              ) : questionList[currentStep].question_type === "true_false" ? (
                <>
                {answerOptions.map((answer) => (
                  <div key={`${answer.id} - ${questionList[currentStep].question_id}-${index}`}>
                    <input type="radio" id={answer.id} name={questionList[currentStep].question_id} value={answer.text} />
                    <label htmlFor={answer.id}>{answer.text}</label>
                    </div>
                    ))}
                    </>
                    ) : (
                <>
                {answerOptions.map((answer) => (
                  <div key={`${answer.id} - ${questionList[currentStep].question_id}-${index}`}>
                    <label htmlFor={answer.id}>{answer.text}</label>
                    <input type="text" id={answer.id} name={questionList[currentStep].question_id} />
                    </div>
                ))}
                </>
              )}
              <div>
              {currentStep > 0 && <Button onClick={handlePreviousSection}>Back</Button>}
              {currentStep < questionList.length - 1 && <Button onClick={handleNextSection}>Next</Button>}
              </div>
              </>
        )}
          </Container>
              </div>
  );
                };
           

export default PriceCalculator;
