import React, { useState, useEffect } from "react";
import {Row, Col, Container, Button} from "react-bootstrap";
import { useNavigate, useLocation } from "react-router-dom";
import axios from "axios";

const AccountConfirmed = () => {

const navigate = useNavigate();
const { search } = useLocation();
const [validToken, setValidToken] = useState(false);
const [errorMessage, setErrorMessage] = useState("");

useEffect(() => {
const params = new URLSearchParams(search);
const token = params.get("token");

if(token){
axios.get(`http://localhost/bachelor_exam/confirm_account.php?token=${token}`)
.then((response) => {
  if(response.data === "Success"){
    sessionStorage.setItem("token", token);
    setValidToken(true);
      } else {
    setErrorMessage(response.data);
  }
  })
.catch((error) => {
  console.log(error);
})
} else {
  navigate("/login");
}
}, [navigate, search]);

  return (
    <>
    <Container>
        <Row>
            <Col sm={5} md={10} xl={12}>
              {errorMessage && <p>{errorMessage}</p>}
            <div className="d-flex align-items-center flex-column p-5" style={{textAlign: "center", marginTop: "12rem"}}>
            <h1 className="mb-4" style={{fontSize: 50}}>Thank you!</h1>
            <p style={{fontSize: 30, width: "90%"}}>Your account has been successfully verified and now you can book a meeting with us</p>
            {validToken === true ? (<Button href="/dashboard" className="mt-4 px-4" style={{
                    fontSize: 25,
                    borderBottom: "4px solid black",
                    borderRadius: 20,
                    backgroundColor: "#7F7EC7",
                  }}>Go to dashboard</Button> ) : (
                    <p>Please wait...</p>
                  )}
            </div>
            </Col>
            </Row>
            </Container>
    </>
  )
}

export default AccountConfirmed
