import React, { useState, useEffect } from "react";
import NavigationPriceCalculator from "./NavigationPriceCalculator";
import { Container } from "react-bootstrap";
import axios from "axios";

const PriceCalculator = () => {

  const [currentStep, setCurrentStep] = useState(0);
  const [questionsBySection, setQuestionsBySection] = useState({});
  const [answers, setAnswers] = useState([]);

  console.log(currentStep);
  console.log("http://localhost/bachelor_exam/question_fetch.php called");

  useEffect(() => {
    console.log("http://localhost/bachelor_exam/question_fetch.php called");
    axios.get(`http://localhost/bachelor_exam/question_fetch.php?section=${currentStep}`)
    .then(response => { 
      console.log(response);
    //   const sections = {};
    //     response.data((question) => {
    //         if(!sections[question.section]) {
    //             sections[question.section] = [];
    //         }
    //         sections[question.section].push(question);
    //     });
    //     setQuestionsBySection(sections);
    // });
}, [currentStep]);

useEffect(() => {
  axios.get(`http://localhost/bachelor_exam/answers.php?question_id=${currentStep}`)
  .then(response => {
    setAnswers(response.data);
  });
}, [currentStep]);


const handleNextSection = () => {
  setCurrentStep(currentStep + 1);
};

const handlePreviousSection = () => {
  setCurrentStep(currentStep - 1);
};


  return (
    <div>
      <NavigationPriceCalculator 
      currentStep={currentStep}
      handleNextSection={handleNextSection}
      handlePreviousSection={handlePreviousSection}
      />
      <Container>
      {Object.keys(questionsBySection).map((section, index) => (
          <div key={index}>
            <h2>{section}</h2>
            {questionsBySection[section].map((question, index) => (
              <div key={question.id}>
                {currentStep === index && (
                  <>
                    <h1>{question.text}</h1>
                    <h3>{question.description}</h3>
                    {answers.map((answer) => (
                      <div key={answer.id}>
                        {question.question_type === "multiple_choice" ? (
                          <>
                            <input type="radio" id={answer.id} name={question.id} value={answer.id} />
                            <label htmlFor={answer.id}>{answer.text}</label>
                          </>
                        ) : (
                          <>
                            <label htmlFor={answer.id}>{answer.text}</label>
                            <input type="text" id={answer.id} name={question.id} />
                          </>
                        )}
                      </div>
                    ))}
                  </>
                )}
              </div>
            ))}
          </div>
        ))}
      </Container>
    </div>
  );
                        })
};

export default PriceCalculator;
