import React, { useState, useEffect} from "react";
import NavigationPriceCalculator from "./NavigationPriceCalculator";
import { Container, Button } from "react-bootstrap";
import axios from "axios";

const PriceCalculator = () => {
  const [currentStep, setCurrentStep] = useState(0);
  const [questionList, setQuestionList] = useState([]);
  const [answerChoices, setAnswerChoices] = useState([]);
  const [input, setInput] = useState("");
  const [question_id, setQuestionId] = useState("");
  const [client_id, setClientId] = useState("");
  const [answerChoicesFetched, setAnswerChoicesFetched] = useState(false);

  useEffect(() => {
    axios
      .get("http://localhost/bachelor_exam/questions.php")
      .then((response) => {
        setQuestionList(response.data);
        
      })
      .catch((error) => {
        console.log(error);
      });
  }, [currentStep]);

  useEffect(() => {
    if(!answerChoicesFetched) {
      axios.get("http://localhost/bachelor_exam/answers.php")
      .then((response) => {
        let answer = response.data;
        if(Array.isArray(answer)) {
          answer = answer.filter((answer)=> answer.question_id === questionList[currentStep].question_id);
          setAnswerChoices(answer);
          setAnswerChoicesFetched(true);
        }
        
      })
      .catch((error) => {
        console.log(error);
      });
    }
  }, [answerChoicesFetched, questionList, currentStep]);

  useEffect(() => {
    if (questionList.length && client_id && question_id) {
      const data = {
        client_id: client_id,
        question_id: question_id
      }
      axios
        .post(
          "http://localhost/bachelor_exam/answers.php", data
        )
        .then((response) => {
          setAnswerChoices(response.data);
          setQuestionId(questionList[currentStep].question_id);
        })
        .catch((error) => {
          console.log(error);
        });
    }
  }, [currentStep, questionList, client_id, question_id]);

  const handleClientId = (event) => {
    setClientId(event.target.value);
  }

  const lastQuestion = questionList.length - 1;

  const handleNextSection = (client_id) => {
    if (currentStep === lastQuestion){
      const result = {
        client_id: client_id,
        question_id: questionList[currentStep].question_id,
      }
      axios.post("http://localhost/bachelor_exam/answers.php", result)
      .then(response => {
        console.log(response);
        window.location.href = "/final";
      })
      .catch(error => {
        console.log(error);
      });
    } else {
    setCurrentStep(currentStep + 1);
};
    }

  const handlePreviousSection = () => {
    setCurrentStep(currentStep - 1);
  };
  
  return (
    <div>
      <NavigationPriceCalculator
      />
      <Container
        fluid
        style={{
          borderTop: "4px solid black",
          borderBottom: "4px solid black",
          textAlign: "center",
          marginTop: "1rem",
          padding: "1rem",
        }}
      >
        {!questionList.length ? (
          <p>Please wait...</p>
        ) : (
          <>
            <h1 className="question_title">{questionList[currentStep].text}</h1>
            <h3 className="question_description">
              {questionList[currentStep].description}
            </h3>
            {questionList[currentStep].question_type &&
            questionList[currentStep].question_type === "multiple_choice" ? (
              <>
                {answerChoices.map((answers) => (
                    <div style={{fontFamily: "secondary-font", fontSize: 20, padding: "0.5rem"}}
                      key={`${answers.answer_id} - ${questionList[currentStep].question_id}`}
                    >
                      <input
                        type="radio"
                        id={answers.answer_id}
                        name={questionList[currentStep].question_id}
                        value={answers.answer_id}
                        onChange={handleClientId}
                        
                      />
                      <label htmlFor={answers.answer_id}>{answers.text}</label>
                    </div>
                  ))}
              </>
            ) : questionList[currentStep].question_type &&
              questionList[currentStep].question_type === "true_false" ? (
              <>
                {answerChoices.map((answers) => (
                    <div style={{fontFamily: "secondary-font", fontSize: 20, padding: "0.5rem"}}
                      key={`${answers.answer_id} - ${questionList[currentStep].question_id}`}
                    >
                      <input
                        type="radio"
                        id={answers.answer_id}
                        name={questionList[currentStep].question_id}
                        value={answers.text}
                        onChange={handleClientId}
                        
                      />
                      <label htmlFor={answers.answer_id}>{answers.text}</label>
                    </div>
                ))}
              </>
            ) : null }
            <div>
              {currentStep > 0 && (
                <Button style={{
                  fontSize: 30,
                  fontWeight: "bold",
                  backgroundColor: "#FF629A",
                  color: "whitesmoke",
                  borderBottom: "4px solid black",
                  marginTop: "3rem",
                }} onClick={handlePreviousSection}>Back</Button>
              )}
              {currentStep <= lastQuestion && (
                <Button style={{
                  fontSize: 30,
                  fontWeight: "bold",
                  backgroundColor: "#FF629A",
                  color: "whitesmoke",
                  borderBottom: "4px solid black",
                  marginTop: "3rem",
                  marginLeft: "3rem",
                }} active type="button" onClick={handleNextSection}>
                  {currentStep === lastQuestion ? "Finish" : "Next"}
                </Button>
              )}
            </div>
          </>
        )}
      </Container>
    </div>
  );
};


export default PriceCalculator;
