import React, {useState, useEffect} from "react";
import NavigationPriceCalculator from "./NavigationPriceCalculator";
import { Container } from "react-bootstrap";
// import { HouseFill, Cart4 } from "react-bootstrap-icons";
import QuestionRow from "./Questions";

const PriceCalculator = () => {

  const [currentStep, setCurrentStep] = useState(0);
  const [questions, setQuestions] = useState([]);

  useEffect(()=>{
    fetch("http://localhost/bachelor_exam/questions_fetch.php")
    .then(response => {
        return response.json()
    })
    .then((result)=>{
        // setQuestion(result);
        console.log(result);
    })
}, []);

const handleNextSection = () => {
  setCurrentStep(currentStep + 1);
};

const handlePreviousSection = () => {
  setCurrentStep(currentStep - 1);
}

const section1Question = questions.filter((q) => q.question_type === "multiple choice ");
const section2Question = questions.filter((q) => q.question_type === "input field");
const section3Question = questions.filter((q) => q.question_type === "true/false");

  return (
    <div>
      <NavigationPriceCalculator 
      currentStep={currentStep}
      handleNextSection={handleNextSection}
      handlePreviousSection={handlePreviousSection}
      />
      <Container>
        {currentStep === 0 && (
          <div>
            {section1Question.map((question) => (
              <QuestionRow key={question.id} question={question} />
            )
            )}
            </div>
        )}
        </Container>
      
    </div>
  );
};

export default PriceCalculator;
