import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { Row, Col, Container, Form, Button } from "react-bootstrap";
import axios from "axios";
import AuthenticationNav from "./AuthenticationNav";

const Login = () => {
  const navigate = useNavigate();

  const [errorMessage, setErrorMessage] = useState("");
  const [userInput, setUserInput] = useState({
    email: "",
    password: "",
  });

  const [user, setUser] = useState(null);

  function emailValidate(email) {
    const emailRegex =
      /[a-zA-Z0-9.!#$%&'*+/=?^_`{|}~-]@(gmail|hotmail|outlook|yahoo)\.com\b$/g;
    return emailRegex.test(email);
  }

  const handleChange = (event) => {
    setUserInput({
      ...userInput, [event.target.name]: event.target.value,
    });
  };

  const handleLogInSubmit = (event) => {
    event.preventDefault();

    axios
      .post("http://localhost/bachelor_exam/login.php", ({
      email: userInput.email,
      password: userInput.password}))
      .then((response) => {
        if (response.data.status === 200) {
          const user = {
            user_id: response.data.user_id,
            first_name: response.data.first_name,
            last_name: response.data.last_name,
            email: response.data.email,
            company: response.data.company,
            phone_number: response.data.phone_number,
            verified: response.data.verified,
            token: response.data.token,
          };
          setUser(user);
          sessionStorage.setItem("user", JSON.stringify(user));
          navigate("/dashboard");
        } else {
          console.log(response.data);
          setErrorMessage("Login failed, try again");
        }
      })
      .catch((error) => {
        if (
          error.response &&
          error.response.data === "All fields must be filled in"
        ) {
          console.log("All fields must be filled in");
          setErrorMessage("All fields must be filled in");
        } else if (
          error.response &&
          error.response.data === "The email is not valid" &&
          !emailValidate()
        ) {
          console.log("The email is not valid");
          setErrorMessage("The email is not valid");
        } else if (
          error.response &&
          error.response.data === "The password is not strong"
        ) {
          console.log("The password is not strong");
          setErrorMessage("The password is not strong");
        } else if (
          error.response &&
          error.response.data ===
            "The password has reached the maximum characters"
        ) {
          console.log("The password has reached the maximum characters");
          setErrorMessage("The password has reached the maximum characters");
        } else if (
          error.response &&
          error.response.data === "Wrong email or password"
        ) {
          console.log("Wrong email or password");
          setErrorMessage("Wrong email or password");
        } else if (
          error.response &&
          error.response.data === "User does not exist"
        ) {
          console.log("User does not exist");
          setErrorMessage("User does not exist");
        } else {
          console.log(error);
        }
      });
  };

  return (
    <>
      <AuthenticationNav />
      <Container fluid>
        <Row>
          <Col
            sm={12}
            md={12}
            lg={5}
            style={{ backgroundColor: "#FF629A", height: "100vh"}}
          ></Col>

          <Col sm={12} md={12} lg={6}>
            <div
              className="text"
              style={{ textAlign: "center", marginTop: "7rem" }}
            >
              <h1 className="mb-2 p-4" style={{ fontSize: 55 }}>
                Log In
              </h1>
              <p className="mb-4" style={{ fontSize: 30 }}>
                Introduce your creditentials in order to log in the portal
              </p>
            </div>
            <Form
              onSubmit={handleLogInSubmit}
              className="py-4 px-5 d-flex align-items-center flex-column"
              style={{ fontSize: 25, fontFamily: "primary-font" }}
            >
              <Form.Group className="mb-3">
                <Form.Control
                  type="email"
                  name="email"
                  placeholder="Email"
                  value={userInput.email}
                  onChange={handleChange}
                  style={{
                    height: "2.7rem",
                    marginBottom: "1rem",
                    padding: "1.5rem",
                    fontSize: 25,
                    borderRadius: 20,
                    borderBottom: "3px solid grey",
                  }}
                />
              </Form.Group>

              <Form.Group className="mb-3">
                <Form.Control
                  type="password"
                  name="password"
                  placeholder="Password"
                  value={userInput.password}
                  autoComplete="off"
                  onChange={handleChange}
                  style={{
                    height: "2.7rem",
                    marginBottom: "1rem",
                    padding: "1.5rem",
                    fontSize: 25,
                    borderRadius: 20,
                    borderBottom: "3px solid grey",
                  }}
                />
              </Form.Group>

              <Button
                type="submit"
                name="submit"
                className="mt-3 mb-5 px-4"
                style={{
                  fontSize: 25,
                  borderBottom: "4px solid black",
                  borderRadius: 20,
                  backgroundColor: "#7F7EC7",
                }}
              >
                Log in
              </Button>

              <p
                style={{
                  color: "red",
                  fontSize: 25,
                  fontFamily: "secondary-font",
                  fontWeight: "bold",
                }}
              >
                {errorMessage}
              </p>

              <p>
                Not registered yet? Sign up{" "}
                <a
                  href="/signup"
                  style={{
                    textDecoration: "none",
                    color: "black",
                    fontWeight: "bold",
                  }}
                >
                  here!
                </a>
              </p>
            </Form>
          </Col>
        </Row>
      </Container>
    </>
  );
};

export default Login;
