import React, { useState } from 'react';
import {Row, Col, Container, Button, Form} from "react-bootstrap";
import LoggedInNavigation from "./LoggedInNavigation";

const UpdateProfile = () => {
  const[first_name, setFirstName] = useState("");
  const[last_name, setLastName] = useState("");
  const[email, setEmail] = useState("");
  const[phone_number, setPhoneNumber] = useState("");
  const[errorMessage, setErrorMessage] = useState("");

  function validateEmail() {
    const emailRegex = /[a-zA-Z0-9.!#$%&'*+/=?^_`{|}~-]@(gmail|hotmail|outlook|yahoo)\.com\b$/g;
    return emailRegex.test(email);
  }

  const UpdateProfileValidation = () => {
    let updateFormValid = true;

    if(!first_name || !last_name || !email || !phone_number){
      setErrorMessage("All the fields must be filled");
      updateFormValid = false;

    } else if(!validateEmail()){
      setErrorMessage("The email you have inserted is not valid");
      updateFormValid = false;

    } else if(phone_number.length !== 8){
      setErrorMessage("Only danish phone number :(");
      updateFormValid = false;

    } else if(first_name.length < 2){
      setErrorMessage("The first name is too short");
      updateFormValid = false;

    } else if(last_name.length < 2){
      setErrorMessage("The last name is too short");
      updateFormValid = false;

    } else {
      setErrorMessage("");
    }
    
    return updateFormValid;
  }

  return (
    <>
    <LoggedInNavigation />
    <Container>
      <Row>
        <Col sm={12} md={10} lg={6}>
    <Form className="py-4 px-5 d-flex align-items-center flex-column" style={{fontFamily: "primary-font"}}>
            <Form.Group className="mb-3">
              <Form.Control type="text" name="first_name" placeholder="Update your first name" value={first_name} onChange={(event) => setFirstName(event.target.value)} style={{height: "3rem", marginBottom: "1rem", padding: "2rem", fontSize: 25, borderRadius: 20, borderBottom: "3px solid grey" }} />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Control type="text" name="last_name" placeholder="Update your last name" value={last_name} onChange={(event) => setLastName(event.target.value)} style={{height: "3rem", marginBottom: "1rem", padding: "1.5rem", fontSize: 25, borderRadius: 20, borderBottom: "3px solid grey" }} />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Control type="email" name="email" placeholder="Update your email" value={email} autoComplete="off" onChange={(event) => setEmail(event.target.value)} style={{ height: "3rem", marginBottom: "1rem", padding: "1.5rem", fontSize: 25, borderRadius: 20, borderBottom: "3px solid grey" }}/>
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Control type="text" name="phone_number" placeholder="Update your phone number" value={phone_number} onChange={(event) => setPhoneNumber(event.target.value)} style={{height: "3rem", marginBottom: "1rem", padding: "1.5rem", fontSize: 25, borderRadius: 20, borderBottom: "3px solid grey" }} />
            </Form.Group>

            <Button type="submit" className="mt-3 mb-5 px-4" style={{fontSize: 25, borderBottom: "4px solid black", borderRadius: 20, backgroundColor: "#7F7EC7"}}>Log in</Button>

            <p style={{color: "red", fontSize: 25, fontFamily: "secondary-font", fontWeight: "bold"}}>{errorMessage}</p>

          </Form>
          </Col>
          </Row>
          </Container>
    </>
  )
}

export default UpdateProfile
