import React, { useState } from "react";
import LoggedInNavigation from "./LoggedInNavigation";
import { Row, Col, Container, Button, Form } from "react-bootstrap";

function Appointment() {
  const [page, setPage] = useState(0);
  const [errorMessage, setErrorMessage] = useState("");
  const [appointmentInput, setAppointmentInput] = useState({
    meeting_date: "",
    meeting_time: "",
    meeting_description: "",
  });
  const [button, setButton] = useState(false);
  const FormTitle = [
    "When would you like to have the meeting?",
    "Can you tell us a small description about your project?",
  ];

  const pageDisplay = () => {
    if (page === 0) {
      return <DateTime />;
    } else if (page === 1) {
      return <Description />;
    }
  };

  const handleChangeInput = (event) => {
    const name = event.target.name;
    const value = event.target.value;
    setAppointmentInput((values) => ({ ...values, [name]: value }));
  };

  const handleAppointmentSubmit = (event) => {
    event.preventDefault();

    //axios.post
  };

  const handleNextPage = () => {
    if (page) {
      handleAppointmentSubmit();
    } else {
      setPage((prevPage) => prevPage + 1);
    }
  };

  const handlePreviousPage = () => {
    setPage((prevPage) => prevPage - 1);
  };

  return (
    <>
      <LoggedInNavigation />
      <Container>
        <h1>{FormTitle[page]}</h1>
        {pageDisplay()}
        {page === 0 && <Button onClick={handlePreviouPage}></Button>}
      </Container>
    </>
  );
}

export default Appointment;
