import React, { Component } from "react";
import { Container, Row, Col, Form, Button } from "react-bootstrap";
import { EnvelopeAt } from "react-bootstrap-icons";

class Contact extends Component {
constructor(props) {
  super(props);
  this.state = {
    full_name: "",
    email: "",
    type_of_inquiry: "",
    inquiry_description: "",
  }
}

render(){
  return (
    <section id="contact-and-support">
      <Container
        fluid
        style={{
          paddingTop: "5rem",
          height: 1400,
          fontFamily: "primary-font",
        }}
      >
        <Row>
          <Col xs={4} lg={8}>
            <div
              style={{
                backgroundColor: "#F9E95D",
                width: 900,
                height: 1505,
                position: "absolute",
                top: 4500,
                left: 0,
                zIndex: -1,
              }}
            ></div>
            <h1
              style={{
                fontSize: 55,
                marginLeft: "8rem",
                position: "relative",
              }}
            >
              Would you like to get in touch with us? Or you need assistance?
            </h1>
            <p
              className="w-75"
              style={{
                fontSize: 25,
                marginTop: "2rem",
                marginLeft: "8rem",
                fontFamily: "secondary-font",
                position: "relative",
              }}
            >
              You can actually do both, feel free to contact us, regardless of
              needing assistance or just a small talk with us.
            </p>
            <Form
              style={{
                width: "45%",
                padding: "0 3rem",
                marginTop: "3rem",
                marginLeft: "5rem",
                fontSize: 25,
              }}
            >
              <Form.Group
                controlId="fullName"
                style={{
                  marginBottom: "2rem",
                }}
              >
                <Form.Label>Your full name</Form.Label>
                <Form.Control
                  onChange={event => handleChange(event)}
                  type="text"
                  name="full_name"
                  style={{
                    borderBottom: "3px solid black",
                    height: "3rem",
                  }}
                ></Form.Control>
              </Form.Group>
              <Form.Group
                controlId="emailAddress"
                style={{
                  marginBottom: "2rem",
                }}
              >
                <Form.Label>Your email address</Form.Label>
                <Form.Control
                  name="email"
                  type="email"
                  style={{
                    borderBottom: "3px solid black",
                    height: "3rem",
                  }}
                ></Form.Control>
              </Form.Group>
              <Form.Group
                controlId="selectInquiry"
                style={{
                  marginBottom: "2rem",
                }}
              >
                <Form.Label>Select the type of inquiry</Form.Label>
                <Form.Select
                  name="type_of_inquiry"
                  style={{
                    borderBottom: "3px solid black",
                    height: "3rem",
                    fontSize: 20,
                  }}
                >
                  <option>Appointment</option>
                  <option>Addressing a question</option>
                  <option>Support/Assistance</option>
                </Form.Select>
              </Form.Group>
              <Form.Group
                controlId="describeInquiry"
                style={{
                  marginBottom: "2rem",
                }}
              >
                <Form.Label>Describe your inquiry</Form.Label>
                <Form.Control
                  
                  type="text"
                  name="inquiry_description"
                  style={{
                    borderBottom: "3px solid black",
                    height: "10rem",
                  }}
                ></Form.Control>
              </Form.Group>
              <Button
                type="submit"
                style={{
                  fontSize: 30,
                  fontWeight: "bold",
                  backgroundColor: "#7F7EC7",
                  color: "whitesmoke",
                  borderBottom: "4px solid black",
                  marginTop: "1rem",
                }}
              >
                Submit
              </Button>{" "}
            </Form>
          </Col>

          <Col xs={4} lg={8}>
            <EnvelopeAt
              size={500}
              style={{
                position: "absolute",
                left: 800,
                top: 5280,
              }}
            ></EnvelopeAt>
          </Col>
        </Row>
      </Container>
    </section>
  );
};
};

export default Contact;
