import React, { useState } from "react";
import LoggedInNavigation from "./LoggedInNavigation";
import { Container, Button, Stack, Row, Col, Form } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import axios from "axios";

//axios.post coming soon for appointment update

const EditAppointment = () => {
  const [errorMessage, setErrorMessage] = useState("");

  const navigate = useNavigate();

  return (
    <>
      <LoggedInNavigation />
      <Container>
        <h1 style={{ marginTop: "5rem", textAlign: "center", fontSize: 50 }}>
          Change appointment
        </h1>
        <p className="mb-4 mt-4 mx-4" style={{ fontSize: 30 }}>
          In the case youâ€™re not able to come to the original time, you can edit
          your meeting here with a new time or date.
        </p>
        <Row>
          <Col sm={2} md={12} lg={12}>
            <Form
              onSubmit={handleAppointmentSubmit}
              className="px-5 my-4 px-5 d-flex justify-content-center flex-column"
              style={{
                fontSize: 25,
                fontFamily: "primary-font",
                marginLeft: "1rem",
                marginRight: "1rem",
              }}
            >
              <Form.Group>
                <Form.Control
                  size="lg"
                  type="date"
                  name="meeting_date"
                  value={appointmentInput.meeting_date}
                  onChange={handleChangeInput}
                  style={{
                    marginBottom: "1rem",
                    padding: "1rem",
                    fontSize: 25,
                    borderRadius: 20,
                    borderBottom: "3px solid grey",
                  }}
                />
              </Form.Group>
              <Form.Group>
                <Form.Control
                  size="lg"
                  type="time"
                  name="meeting_time"
                  value={appointmentInput.meeting_time}
                  onChange={handleChangeInput}
                  style={{
                    marginBottom: "0.5rem",
                    padding: "1rem",
                    fontSize: 25,
                    borderRadius: 20,
                    borderBottom: "3px solid grey",
                  }}
                />
              </Form.Group>
              <Form.Group>
                <Form.Control
                  as="textarea"
                  name="meeting_description"
                  value={appointmentInput.meeting_description}
                  onChange={handleChangeInput}
                  rows={4}
                  placeholder="Describe your upcoming project here..."
                  style={{
                    padding: "1rem 3rem 3rem 1.5rem",
                    fontSize: 25,
                    fontFamily: "secondary-font",
                    borderRadius: 20,
                    borderBottom: "4px solid grey",
                  }}
                />
              </Form.Group>
              <Stack
                direction="horizontal"
                gap={5}
                className="d-flex justify-content-center"
              >
                <Button
                  className="mt-4 px-4"
                  style={{
                    fontSize: 30,
                    fontFamily: "primary-font",
                    borderBottom: "4px solid black",
                    borderRadius: 20,
                    backgroundColor: "#7F7EC7",
                    marginBottom: "2rem",
                  }}
                  onClick={handleAppointmentSubmit}
                >
                  Confirm
                </Button>
              </Stack>
              <p
                style={{
                  color: "red",
                  fontSize: 30,
                  fontFamily: "secondary-font",
                  fontWeight: "bold",
                  textAlign: "center",
                }}
              >
                {errorMessage}
              </p>
            </Form>
          </Col>
        </Row>
      </Container>
    </>
  );
};

export default EditAppointment;
