import React, { useState, useEffect } from "react";
import LoggedInNavigation from "./LoggedInNavigation";
import { Row, Col, Container, Button, Stack } from "react-bootstrap";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const CancelAppointment = () => {
  const navigate = useNavigate();
  const [errorMessage, setErrorMessage] = useState("");
  const [appointments, setAppointments] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost/api/get_user.php", {
        withCredentials: true,
      })
      .then((response) => {
        if (response.data) {
          navigate("/cancelappointment");
        } else {
          navigate("/login");
        }
      });
  }, [navigate]);

  useEffect(() => {
    axios
      .get("http://localhost/api/check_meetings.php", {
        withCredentials: true,
      })
      .then((response) => {
        if (response.data) {
          setAppointments(response.data);
        }
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  const handleDeleteSubmit = () => {
    axios
      .delete(
        `http://localhost/api/cancel_meeting.php?meeting_id=${appointments.meeting_id}`,
        { meeting_id: appointments.meeting_id },
        {
          withCredentials: true,
        }
      )
      .then((response) => {
        if (response.data) {
          navigate(
            `/cancelledappointmentconfirm?meeting_id=${appointments.meeting_id}`
          );
          setErrorMessage("");
        } else {
          setErrorMessage("Meeting not found");
        }
      })
      .catch((error) => {
        console.log(error);
      });
  };

  return (
    <>
      <LoggedInNavigation />
      {appointments && (
        <Container>
          <Row>
            <Col sm={5} md={10} xl={12}>
              <div
                className="d-flex align-items-center flex-column p-5"
                style={{ textAlign: "center", marginTop: "5rem" }}
              >
                <h1 className="mb-5" style={{ fontSize: 50 }}>
                  Are you sure you want to cancel the meeting?
                </h1>
                <p style={{ fontSize: 30, width: "90%" }}>
                  In case you reconsider, you can reschedule the meeting
                  anytime.
                </p>
                <Stack
                  gap={4}
                  direction="horizontal"
                  className="d-flex justify-content-center"
                >
                  <Button
                    onClick={handleDeleteSubmit}
                    className="mt-4 px-4"
                    style={{
                      fontSize: 30,
                      fontFamily: "primary-font",
                      borderBottom: "4px solid black",
                      borderRadius: 20,
                      backgroundColor: "#EB3131",
                    }}
                  >
                    Yes
                  </Button>

                  <Button
                    href="/appointmentlist"
                    className="mt-4 px-4"
                    style={{
                      fontSize: 30,
                      fontFamily: "primary-font",
                      borderBottom: "4px solid black",
                      borderRadius: 20,
                      backgroundColor: "#7F7EC7",
                    }}
                  >
                    No, take me back
                  </Button>
                </Stack>
                <p
                  style={{
                    color: "red",
                    fontSize: 30,
                    fontFamily: "secondary-font",
                    fontWeight: "bold",
                    textAlign: "center",
                  }}
                >
                  {errorMessage}
                </p>
              </div>
            </Col>
          </Row>
        </Container>
      )}
    </>
  );
};

export default CancelAppointment;
