import React, { useState } from "react";
import LoggedInNavigation from "./LoggedInNavigation";
import { Container, Button, Stack } from "react-bootstrap";
import DateTime from "./DateTime";
import Description from "./Description";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const AddAppointment = () => {
  const [page, setPage] = useState(0);
  const [errorMessage, setErrorMessage] = useState("");
  const [disableButton, setDisableButton] = useState(false);
  const FormTitle = [
    "When would you like to have the meeting?",
    "Can you tell us a small description about your project?",
  ];
  const navigate = useNavigate();

  const pageDisplay = () => {
    if (page === 0) {
      return (
        <DateTime
          value={appointmentInput}
          onChange={handleChangeInput}
          errorMessage={errorMessage}
        />
      );
    } else if (page === 1) {
      return (
        <Description
          value={appointmentInput.meeting_description}
          onChange={handleChangeInput}
          errorMessage={errorMessage}
        />
      );
    }
  };

  const handleChangeInput = (event) => {
    const name = event.target.name;
    const value = event.target.value;
    setAppointmentInput((values) => ({ ...values, [name]: value }));
  };

  const handleAppointmentSubmit = (event) => {
    event.preventDefault();

    axios
      .post("http://localhost/api/add_meeting.php", { withCredentials: true })
      .then((response) => {
        if (response.data) {
          navigate("/appointmentconfirm");
          setErrorMessage("");
        } else {
          setErrorMessage("Appointment submitting failed");
          setDisableButton(true);
        }
      })
      .catch((error) => {
        if (
          error.response &&
          error.response.data === "You must choose a date"
        ) {
          setErrorMessage("You must choose a date");
          setDisableButton(true);
        }
      });
  };

  const handleNextPage = () => {
    if (page === 1) {
      handleAppointmentSubmit();
    } else if (errorMessage) {
      setDisableButton(true);
    } else {
      setPage((prevPage) => prevPage + 1);
    }
  };

  const handlePreviousPage = () => {
    setPage((prevPage) => prevPage - 1);
  };

  return (
    <>
      <LoggedInNavigation />
      <Container>
        <h1 style={{ marginTop: "5rem", textAlign: "center", fontSize: 50 }}>
          {FormTitle[page]}
        </h1>
        {pageDisplay()}
        <Stack
          direction="horizontal"
          gap={5}
          className="d-flex justify-content-center"
        >
          {page === 1 && (
            <Button
              className="mt-4 px-4"
              style={{
                fontSize: 30,
                fontFamily: "primary-font",
                borderBottom: "4px solid black",
                borderRadius: 20,
                backgroundColor: "#7F7EC7",
                marginBottom: "2rem",
              }}
              onClick={handlePreviousPage}
              disabled={page === 0}
            >
              Back
            </Button>
          )}
          {page === 0 && (
            <Button
              className="mt-4 px-4"
              style={{
                fontSize: 30,
                fontFamily: "primary-font",
                borderBottom: "4px solid black",
                borderRadius: 20,
                backgroundColor: "#7F7EC7",
                marginBottom: "2rem",
              }}
              onClick={handleNextPage}
              disabled={disableButton}
            >
              Next
            </Button>
          )}
          {page === 1 && (
            <Button
              className="mt-4 px-4"
              style={{
                fontSize: 30,
                fontFamily: "primary-font",
                borderBottom: "4px solid black",
                borderRadius: 20,
                backgroundColor: "#7F7EC7",
                marginBottom: "2rem",
              }}
              onClick={handleAppointmentSubmit}
              disabled={disableButton}
            >
              Confirm
            </Button>
          )}
        </Stack>
        <p
          style={{
            color: "red",
            fontSize: 25,
            fontFamily: "secondary-font",
            fontWeight: "bold",
          }}
        >
          {errorMessage}
        </p>
      </Container>
    </>
  );
};

export default AddAppointment;
