import React, { useState, useEffect } from "react";
import NavigationPriceCalculator from "./NavigationPriceCalculator";
import { Container } from "react-bootstrap";
import axios from "axios";

const PriceCalculator = () => {

  const [currentStep, setCurrentStep] = useState(0);
  const [questions, setQuestions] = useState([]);
  const [answers, setAnswers] = useState([]);

  useEffect(() => {
    axios.get("http://localhost/bachelor_exam/questions.php")
    .then(response => { 
    console.log(response);
    const questionArray = response.data.map(({ question_id, text, description, question_type}) => ({
      question_id,
      text,
      description,
      question_type
    }))
    setQuestions(questionArray);
  })
      .catch(error => {
        console.log(error);
      });
}, []);

// useEffect(() => {
//   axios.get(`http://localhost/bachelor_exam/answers.php?question_id=${questions[currentStep].id}`)
//   .then(response => {
//     setAnswers(response.data);
//   })
//   .catch(error => {
//     console.log(error);
//   })
// }, [currentStep, questions]);


const handleNextSection = () => {
  if(currentStep + 1 < questions.length) {
  setCurrentStep(currentStep + 1);
  }
};

const handlePreviousSection = () => {
  if(currentStep > 0){
  setCurrentStep(currentStep - 1);
  }
};


  return (
    <div>
      <NavigationPriceCalculator 
      currentStep={currentStep}
      handleNextSection={handleNextSection}
      handlePreviousSection={handlePreviousSection}
      />
      <Container>
        <h1>{questions[currentStep].text}</h1>
        <h3>{questions[currentStep].description}</h3>
        {questions[currentStep].question_type === "multiple_choice" ? (
          <>
          {answers.map((answer) => (
            <div key={answer.id}>
              <input type="radio" id={answer.id} name={questions[currentStep].question_id} value={answer.id} />
              <label htmlFor={answer.id}>{answer.text}</label>
              </div>
          ))}
              </>
              ) :  questions[currentStep].question_type === "true_false" ? (
                <>
                {answers.map((answer) => (
                  <div key={answer.id}>
                    <input type="radio" id={answer.id} name={questions[currentStep].question_id} value={answer.text} />
                    <label htmlFor={answer.id}>{answer.text}</label>
                    </div>
                    ))}
                    </>
                    ) : (
                <>
                {answers.map((answer) => (
                  <div key={answer.id}>
                    <label htmlFor={answer.id}>{answer.text}</label>
                    <input type="text" id={answer.id} name={questions[currentStep].question_id} />
                    </div>
                ))}
                </>
              )}
              </Container>
              </div>
  );
                };
           

export default PriceCalculator;
