import React, { useEffect, useState } from "react";
import LoggedInNavigation from "./LoggedInNavigation";
import { Row, Col, Container, Button, Table } from "react-bootstrap";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const AppointmentList = () => {
  const navigate = useNavigate();
  const [appointments, setAppointments] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost/api/get_user.php", { withCredentials: true })
      .then((response) => {
        if (response.data) {
          navigate("/appointmentlist");
        } else {
          navigate("/login");
        }
      });
  }, [navigate]);

  useEffect(() => {
    axios
      .get("http://localhost/api/check_meetings.php", { withCredentials: true })
      .then((response) => {
        if (response.data) {
          setAppointments(response.data);
        }
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  return (
    <>
      <LoggedInNavigation />
      {appointments.length > 0 ? (
        <Container>
          <h1
            className="mb-5"
            style={{ fontSize: 50, marginTop: "3rem", textAlign: "center" }}
          >
            Your meetings
          </h1>
          <Table striped bordered hover>
            <thead>
              <tr>
                <th>Meeting Date</th>
                <th>Meeting Time</th>
                <th>Meeting Description</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {appointments.map((appointment) => (
                <tr key={appointment.id}>
                  <td>{appointment.meeting_date}</td>
                  <td>{appointment.meeting_time}</td>
                  <td>{appointment.meeting_description}</td>
                  <td>
                    <Button
                      href="/editappointment"
                      className="mt-2 px-4"
                      style={{
                        fontSize: 20,
                        fontFamily: "primary-font",
                        borderBottom: "4px solid black",
                        borderRadius: 20,
                        backgroundColor: "#7F7EC7",
                      }}
                    >
                      Edit
                    </Button>
                    <Button
                      href="/cancelappointment"
                      className="my-3 px-4"
                      style={{
                        fontSize: 20,
                        fontFamily: "primary-font",
                        borderBottom: "4px solid black",
                        borderRadius: 20,
                        backgroundColor: "#EB3131",
                      }}
                    >
                      Cancel
                    </Button>
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>
          <div className="d-flex justify-content-center">
            <Button
              href="/addappointment"
              className="mt-4 px-4 mb-5"
              style={{
                fontSize: 30,
                fontFamily: "primary-font",
                borderBottom: "4px solid black",
                borderRadius: 20,
                backgroundColor: "#7F7EC7",
              }}
            >
              Book a new meeting
            </Button>
          </div>
        </Container>
      ) : (
        <Container>
          <Row>
            <Col sm={5} md={10} xl={12}>
              <div
                className="d-flex align-items-center flex-column p-5"
                style={{ textAlign: "center", marginTop: "5rem" }}
              >
                <h1 className="mb-5" style={{ fontSize: 50 }}>
                  Whoops, there's nothing here.
                </h1>
                <p style={{ fontSize: 30, width: "90%" }}>
                  Would you like to book a meeting with us to discuss your
                  project?
                </p>
                <Button
                  href="/addappointment"
                  className="mt-4 px-4"
                  style={{
                    fontSize: 30,
                    fontFamily: "primary-font",
                    borderBottom: "4px solid black",
                    borderRadius: 20,
                    backgroundColor: "#7F7EC7",
                  }}
                >
                  Book a new meeting
                </Button>
              </div>
            </Col>
          </Row>
        </Container>
      )}
    </>
  );
};

export default AppointmentList;
