import React from "react";
import { Container, Row, Col } from "react-bootstrap";
import { PersonFill } from "react-bootstrap-icons";
import { useTranslation } from "react-i18next";

const About = () => {
  const { t } = useTranslation();

  return (
    <section id="about-us">
      <Container
        className="container-about"
        fluid
        style={{
          backgroundColor: "#EBBFF3",
          textAlign: "center",
          fontFamily: "primary-font",
        }}
      >
        <h1 style={{ fontSize: 55 }}>Designers.Developers.Nuubs.</h1>
        <p
          style={{
            fontSize: 25,
            width: "70%",
            marginTop: "2rem",
            marginLeft: "13rem",
            fontFamily: "secondary-font",
          }}
        >
          {t(
            "This is us, everyday, we are a group of dedicated people in the web domain who always put the professionalism on the first place, equipped with solid knowledge, training and experience, and proud of our profession, always ready to deliver the best of your product!"
          )}
        </p>
        <Row
          className="g-4"
          style={{
            display: "flex",
            justifyContent: "center",
            marginTop: "2rem",
            marginLeft: "12rem",
          }}
        >
          <Col lg={5}>
            <div
              style={{
                width: 270,
                height: 270,
                borderRadius: "50%",
                backgroundColor: "whitesmoke",
              }}
            >
              <PersonFill size={200} className="mt-4"></PersonFill>
            </div>
            <div
              style={{
                textAlign: "center",
                marginRight: "17rem",
                marginTop: "1rem",
              }}
            >
              <h1 style={{ fontSize: 45 }}>Kristian Lerche</h1>
              <p style={{ fontSize: 25, fontFamily: "secondary-font" }}>
                {t("Owner of Nuub")}
              </p>
            </div>
          </Col>
          <Col lg={5}>
            <div
              style={{
                width: 270,
                height: 270,
                borderRadius: "50%",
                backgroundColor: "whitesmoke",
              }}
            >
              <PersonFill size={200} className="mt-4"></PersonFill>
            </div>
            <div
              style={{
                textAlign: "center",
                marginRight: "17rem",
                marginTop: "1rem",
              }}
            >
              <h1 style={{ fontSize: 45 }}>Philip Marlow</h1>
              <p style={{ fontSize: 25, fontFamily: "secondary-font" }}>
                {t("Senior Web Designer & Project Management")}
              </p>
            </div>
          </Col>
          <Col lg={5}>
            <div
              style={{
                width: 270,
                height: 270,
                borderRadius: "50%",
                backgroundColor: "whitesmoke",
                marginTop: "3rem",
              }}
            >
              <PersonFill size={200} className="mt-4"></PersonFill>
            </div>
            <div
              style={{
                textAlign: "center",
                marginRight: "17rem",
                marginTop: "1rem",
              }}
            >
              <h1 style={{ fontSize: 45 }}>Betina Svedsen</h1>
              <p style={{ fontSize: 25, fontFamily: "secondary-font" }}>
                {t("Web and Graphic Designer")}
              </p>
            </div>
          </Col>
          <Col lg={5}>
            <div
              style={{
                width: 270,
                height: 270,
                borderRadius: "50%",
                backgroundColor: "whitesmoke",
                marginTop: "3rem",
              }}
            >
              <PersonFill size={200} className="mt-4"></PersonFill>
            </div>
            <div
              style={{
                textAlign: "center",
                marginRight: "17rem",
                marginTop: "1rem",
              }}
            >
              <h1 style={{ fontSize: 45 }}>Iga Naskręt</h1>
              <p style={{ fontSize: 25, fontFamily: "secondary-font" }}>
                {t("Web Developer")}
              </p>
            </div>
          </Col>
        </Row>
      </Container>
    </section>
  );
};

export default About;
