import React, { useState } from "react";
import LoggedInNavigation from "./LoggedInNavigation";
import { Container, Button, Stack, Row, Col, Form } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const AddAppointment = () => {
  const [page, setPage] = useState(0);
  const [errorMessage, setErrorMessage] = useState("");
  const [appointmentInput, setAppointmentInput] = useState({
    meeting_date: "",
    meeting_time: "",
    meeting_description: "",
  });
  const navigate = useNavigate();

  const FormTitle = [
    "When would you like to have the meeting?",
    "Can you tell us a small description about your project?",
  ];

  const handleChangeInput = (event) => {
    const name = event.target.name;
    const value = event.target.value;
    setAppointmentInput((prevAppointmentInput) => ({
      ...prevAppointmentInput,
      [name]: value,
    }));
  };

  const handleAppointmentSubmit = (event) => {
    event.preventDefault();

    axios
      .post("http://localhost/api/add_meeting.php", { withCredentials: true })
      .then((response) => {
        if (response.data) {
          navigate("/appointmentconfirm");
          setErrorMessage("");
        } else {
          setErrorMessage("Appointment submitting failed");
        }
      })
      .catch((error) => {
        if (
          error.response &&
          error.response.data === "You must choose a date"
        ) {
          setErrorMessage("You must choose a date");
        }
      });
  };

  return (
    <>
      <LoggedInNavigation />
      <Container>
        <h1 style={{ marginTop: "5rem", textAlign: "center", fontSize: 50 }}>
          {FormTitle[page]}
        </h1>
        <Row>
          <Col sm={5} md={12} lg={12}>
            <>
              <Form.Group>
                <Form.Control
                  size="lg"
                  type="date"
                  name="meeting_date"
                  value={appointmentInput.meeting_date}
                  onChange={handleChangeInput}
                  style={{
                    marginBottom: "1rem",
                    padding: "1rem",
                    fontSize: 25,
                    borderRadius: 20,
                    borderBottom: "3px solid grey",
                  }}
                />
              </Form.Group>
              <Form.Group>
                <Form.Control
                  size="lg"
                  type="time"
                  name="meeting_time"
                  value={appointmentInput.meeting_time}
                  onChange={handleChangeInput}
                  style={{
                    marginBottom: "0.5rem",
                    padding: "1rem",
                    fontSize: 25,
                    borderRadius: 20,
                    borderBottom: "3px solid grey",
                  }}
                />
              </Form.Group>
              <Form.Group>
                <Form.Control
                  as="textarea"
                  name="meeting_description"
                  value={appointmentInput.meeting_description}
                  onChange={handleChangeInput}
                  rows={4}
                  placeholder="Describe your upcoming project here..."
                  style={{
                    padding: "1rem 3rem 3rem 1.5rem",
                    fontSize: 25,
                    fontFamily: "secondary-font",
                    borderRadius: 20,
                    borderBottom: "4px solid grey",
                  }}
                />
              </Form.Group>
              <p
                style={{
                  color: "red",
                  fontSize: 25,
                  fontFamily: "secondary-font",
                  fontWeight: "bold",
                }}
              >
                {errorMessage}
              </p>
            </>
          </Col>
        </Row>
        <Stack
          direction="horizontal"
          gap={5}
          className="d-flex justify-content-center"
        >
          <Button
            className="mt-4 px-4"
            style={{
              fontSize: 30,
              fontFamily: "primary-font",
              borderBottom: "4px solid black",
              borderRadius: 20,
              backgroundColor: "#7F7EC7",
              marginBottom: "2rem",
            }}
            onClick={handleAppointmentSubmit}
          >
            Confirm
          </Button>
        </Stack>
        <p
          style={{
            color: "red",
            fontSize: 25,
            fontFamily: "secondary-font",
            fontWeight: "bold",
          }}
        >
          {errorMessage}
        </p>
      </Container>
    </>
  );
};

export default AddAppointment;
